from django.db import models# Create your models here.class IDC(models.Model):    """ 机房信息 """    name = models.CharField(max_length=32, verbose_name='机房名称')    address = models.CharField(max_length=200, verbose_name='机房地址')    phone = models.CharField(max_length=20, verbose_name='联系电话')    email = models.EmailField(verbose_name='联系邮件', default='null')    letter = models.CharField(max_length=8, verbose_name='机房简称')    class Meta:        ordering = ['id']        db_table = 'resource_idc'    def __str__(self):        return self.name